import { useEffect } from "react";

const Home = () => {
  //Windows height adjustment
  const fullHeight = () => {
    const elements = document.querySelectorAll(".js-fullheight");

    const setFullHeight = () => {
      const windowHeight =
        window.innerHeight || document.documentElement.clientHeight;
      elements.forEach((element) => {
        element.style.height = windowHeight + "px";
      });
    };
    setFullHeight();
    window.addEventListener("resize", setFullHeight);
  };

  //Keypress Handler
  const handleKeyPress = (event) => {
    if (
      (event.metaKey && event.keyCode === 75) ||
      (event.ctrlKey && event.keyCode === 75)
    ) {
      console.log("done");
      const searchBar = document.getElementById("search");
      searchBar.focus();
      event.preventDefault();
    }
  };

  useEffect(() => {
    // Height function call
    fullHeight();

    const cmd_icons = document.getElementById("search_cmd");
    if (navigator.userAgent.indexOf("Mac OS X") != -1) {
      cmd_icons.innerHTML = "⌘ k";
    } else {
      cmd_icons.innerHTML = "ctrl + k";
    }
    const searchBar = document.getElementById("search");
    const searchDiv = document.getElementById("inner_form");
    // Add event listener for focus
    searchBar.addEventListener("focus", function () {
      searchDiv.style.boxShadow = "0px 2px 79px -7px rgba(62,100,255,0.73)";
    });
    // Add event listener for blur
    searchBar.addEventListener("blur", function () {
      searchDiv.style.boxShadow = "none";
    });

    document.addEventListener("keydown", handleKeyPress);

    return () => {
      document.removeEventListener("keydown", handleKeyPress);
    };
  }, []);

  return (
    <>
      <div className="wrapper d-flex align-items-stretch js-fullheight">
        <nav id="sidebar">
          <h1>
            <a href="index.html" className="logo" id="logo">
              Snippet.
            </a>
          </h1>
          <ul className="list-unstyled components mb-5">
            <li className="active">
              <div className="list-heading">
                <span className="fa fa-home"></span>
                <h5>Categories</h5>
              </div>

              <div className="form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  defaultValue
                  id="flexCheckDefault"
                />
                <label className="form-check-label" htmlFor="flexCheckDefault">
                  HTML
                </label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  defaultValue
                  id="flexCheckDefault"
                />
                <label className="form-check-label" htmlFor="flexCheckDefault">
                  SCSS
                </label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  defaultValue
                  id="flexCheckDefault"
                />
                <label className="form-check-label" htmlFor="flexCheckDefault">
                  JAVASCRIPT
                </label>
              </div>
              <div className="form-check">
                <input
                  className="form-check-input"
                  type="checkbox"
                  defaultValue
                  id="flexCheckDefault"
                />
                <label className="form-check-label" htmlFor="flexCheckDefault">
                  PHP
                </label>
              </div>
            </li>
          </ul>
        </nav>
        {/* Page Content  */}
        <div id="content" className="p-4 p-md-5">
          <nav className="navbar navbar-expand-lg navbar-light bg-light">
            <div className="container-fluid">
              <div className="search-wrap col-md-12">
                <div className="inner-form" id="inner_form">
                  <div className="input-field first-wrap">
                    <div className="input-select">
                      <i className="fa-solid fa-caret-down"></i>
                      <select
                        data-trigger
                        name="choices-single-defaul"
                        id="select-category"
                      >
                        <option>Category</option>
                        <option>New Arrivals</option>
                        <option>Sale</option>
                        <option>Ladies</option>
                        <option>Men</option>
                        <option>Clothing</option>
                        <option>Footwear</option>
                        <option>Accessories</option>
                      </select>
                    </div>
                  </div>
                  <div className="input-field second-wrap">
                    <span id="search_cmd"></span>
                    <input
                      id="search"
                      type="text"
                      placeholder="Do you want to find something?"
                    />
                  </div>
                  <div className="input-field third-wrap">
                    <button className="btn-search" type="button">
                      <i className="fa-solid fa-magnifying-glass"></i>
                    </button>
                  </div>
                </div>
              </div>
            </div>
          </nav>
          <h2 className="mb-4">Sidebar #07</h2>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat. Duis aute irure dolor in
            reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
            pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
            culpa qui officia deserunt mollit anim id est laborum.
          </p>
          <p>
            Lorem ipsum dolor sit amet, consectetur adipisicing elit, sed do
            eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim
            ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut
            aliquip ex ea commodo consequat. Duis aute irure dolor in
            reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla
            pariatur. Excepteur sint occaecat cupidatat non proident, sunt in
            culpa qui officia deserunt mollit anim id est laborum.
          </p>
        </div>
      </div>
    </>
  );
};

export default Home;
