import { Link } from "react-router-dom";
import { dataContext } from "./Home";
import { useContext, useEffect, useState } from "react";

const Sidebar = () => {
  const [data, setData] = useContext(dataContext);
  const [foundElement, setElement] = useState("false");
  const uniqueValues = [];

  const setUniqueValues = () => {
    if (Array.isArray(data))
      var uniqueArray = data.map((value) => {
        if (!uniqueValues.includes(value.category)) {
          uniqueValues.push(value.category);
          return value.category;
        }
        return null; // or any value to indicate a repeated value
      });
    console.log("result", uniqueArray);
  };
  useEffect(() => {}, [uniqueValues]);
  return (
    <nav id="sidebar">
      <h1>
        <Link to="/" className="logo" id="logo">
          Snippet.
        </Link>
      </h1>
      <ul className="list-unstyled components mb-5">
        <li className="active">
          <div className="list-heading">
            <Link to="/add" className="btn-addSnippet">
              <span className="fa fa-plus"></span>
              Add Snippet
            </Link>
          </div>
        </li>
        <li className="active">
          <div className="list-heading">
            <a href="#">
              <span className="fa fa-home"></span>
              Categories
            </a>
          </div>
          {console.log("uniqu", uniqueValues)}
          {uniqueValues.map((categories) => (
            <div className="form-check">
              <input
                className="form-check-input"
                type="checkbox"
                id={categories}
              />
              <label className="form-check-label" htmlFor={categories}>
                {categories}
              </label>
            </div>
          ))}
        </li>
      </ul>
    </nav>
  );
};

export default Sidebar;
