import { Link } from "react-router-dom";
import { dataContext } from "./Home";
import { useContext, useEffect, useState } from "react";

const Sidebar = () => {
  const [data, setData] = useContext(dataContext);
  const [foundElement, setElement] = useState("false");
  let arr = [];
  useEffect(() => {
    // Array.isArray(data)
    //   ? data.map((cat) => {
