    const uniqueValues = [];
    if (Array.isArray(data))
      var uniqueArray = data.map((value) => {
        if (!uniqueValues.includes(value)) {
          uniqueValues.push(value);
          return value;
        }
        return null; // or any value to indicate a repeated value
      });
    console.log("result", uniqueArray);
  }, [data]);
  return (
    <nav id="sidebar">
      <h1>
        <Link to="/" className="logo" id="logo">
          Snippet.
        </Link>
      </h1>
      <ul className="list-unstyled components mb-5">
        <li className="active">
          <div className="list-heading">
            <Link to="/add" className="btn-addSnippet">
              <span className="fa fa-plus"></span>
              Add Snippet
            </Link>
          </div>
        </li>
        <li className="active">
          <div className="list-heading">
            <a href="#">
              <span className="fa fa-home"></span>
              Categories
            </a>
          </div>
          {console.log(arr)}
          {Array.isArray(data)
            ? data.map((categories) => (
                <div className="form-check">
                  <input
                    className="form-check-input"
                    type="checkbox"
                    id={categories.category}
                  />
                  <label
                    className="form-check-label"
                    htmlFor={categories.category}
                  >
                    {categories.category}
                  </label>
                </div>
              ))
            : ""}
        </li>
      </ul>
    </nav>
  );
};

export default Sidebar;
