import image from "../code.jpg";
import Prism from "prismjs";
import { useState } from "react";

const Single = () => {
  const [state, setstate] = useState(
    'p className="snippet-description">Snippet description goes here.</p>'
  );

  const html = Prism.highlight(state, Prism.languages.html, "html");
  return (
    <div className="container">
      <div className="row">
        <div className="col-lg-12">
          <div className="snippet-card">
            <div className="snippet-picture">
              <img src={image} alt="picture" />
            </div>
            <h1 className="snippet-title">Snippet Title</h1>
            <p className="snippet-description">
              Snippet description goes here.
            </p>
            <pre>
              <code class="language-javascript">{html}</code>
            </pre>

            <pre className="snippet-preview">
              <small class="snippetTag">HTML</small>
              Lorem Ipsum is simply dummy text of the printing and typesetting
              industry. Lorem Ipsum has been the industry's standard dummy text
              ever since the 1500s, when an unknown printer took a galley of
              type and scrambled it to make a type specimen book. It has
              survived not only five centuries, but also the leap into
              electronic typesetting, remaining essentially unchanged. It was
              popularised in the 1960s with the release of Letraset sheets
              containing Lorem Ipsum passages, and more recently with desktop
              publishing software like Aldus PageMaker including versions of
              Lorem Ipsum.
            </pre>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Single;
