import { useEffect } from "react";
import { Component } from "react";

const Home = () => {
    componentDidMount= () =>{
        document.addEventListener('keydown', this.handleKeyPress);
      }

      componentWillUnmount = () => {
        document.removeEventListener('keydown', this.handleKeyPress);
      }

      handleKeyPress = (event) => {
        // Check for desired key combination: Cmd + W (Mac) or Ctrl + W (Windows)
        if ((event.metaKey && event.keyCode === 87) || (event.ctrlKey && event.keyCode === 87)) {
          // Get the search bar element
          const searchBar = document.getElementById('search-bar');

          // Focus on the search bar
          searchBar.focus();

          // Prevent the default behavior of the key combination
          event.preventDefault();
        }
      }

    //Windows height adjustment
    const fullHeight = () => {
        const elements = document.querySelectorAll('.js-fullheight');

        const setFullHeight = () => {
          const windowHeight = window.innerHeight || document.documentElement.clientHeight;
          elements.forEach(element => {
            element.style.height = windowHeight + 'px';
          });
        };

        setFullHeight();

        window.addEventListener('resize', setFullHeight);
      };

    useEffect(() => {


          fullHeight();


